mapper.select:
SELECT CLASS.*, P.*
FROM (
        SELECT *
        FROM class c JOIN review r
            ON c.CLASS_NUMBER = r.CLASS_NUMBER
            AND c.CLASS_NUMBER = #{classNumber}
        ORDER BY r.REVIEW_UPDATE_DATE desc
) CLASS JOIN PRO P
ON CLASS.PRO_NUMBER = P.PRO_NUMBER
AND CLASS.PRO_NUMBER = #{proNumber}

mapper.update:
update REVIEW
set REVIEW_CONTENT = #{reviewContent}, REVIEW_UPDATE_DATE = sysdate
where REVIEW_NUMBER = #{reviewNumber}
