mapper.select:
SELECT PARENT_NUMBER
FROM PARENT_POST
WHERE PARENT_POST_NUMBER = #{parentPostNumber}

mapper.update:
UPDATE PARENT_POST
SET PARENT_POST_REPORTCOUNT = PARENT_POST_REPORTCOUNT + 1
WHERE PARENT_POST_NUMBER = #{parentPostNumber}

UPDATE PARENT
SET PARENT_REPORT_COUNT = PARENT_REPORT_COUNT + 1
WHERE PARENT_NUMBER = #{parentNumber}
